import React,{Component} from 'react';
import Todoitem from "./Todoitem";

import TodoForm from './TodoForm';



const APIURL='/api/todos/';


class TodoList extends Component{
    constructor(props){
        super (props);
        this.state={
            todos:[]
        }

    }
    componentWillMount(){
        this.loadTodos();
    }

    loadTodos(){
        fetch(APIURL)
            .then((resp)=>{
                if(!resp.ok) {
                    if (resp.status >= 400 && resp.status < 500) {
                        return resp.json().then(data => {
                            let err = {whatever: data.message};
                            throw err;
                        })
                    } else {
                        let err = {whatever: 'Please try again later, server is not responding'}
                        throw err;
                    }
                }
                return resp.json();
            })
            //setState means we should change the todos in state
            .then(todos=> {
                this.setState({todos})
            });
    }
    //take second argument
    addTodo(val){
        fetch(APIURL,{
            method:'post',
            headers: new Headers({
                'Content-Type':'application/json',}),
            body: JSON.stringify({name:val})


        })
            .then((resp)=>{
                if(!resp.ok) {
                    if (resp.status >= 400 && resp.status < 500) {
                        return resp.json().then(data => {
                            let err = {whatever: data.message};
                            throw err;
                        })
                    } else {
                        let err = {whatever: 'Please try again later, server is not responding'}
                        throw err;
                    }
                }
                return resp.json();
            }).then(newTodo=>{
                //concatanation array
                this.setState({todos:[...this.state.todos,newTodo]})
        })
    }
    deleteTodo(id){
        const deleteURL=APIURL+ id;
        fetch(deleteURL,{
            method:'delete'})
            .then((resp)=>{
                if(!resp.ok) {
                    if (resp.status >= 400 && resp.status < 500) {
                        return resp.json().then(data => {
                            let err = {whatever: data.message};
                            throw err;
                        })
                    } else {
                        let err = {whatever: 'Please try again later, server is not responding'}
                        throw err;
                    }
                }
                return resp.json();
            }).then(()=>{
            //concatanation array
            const todos=this.state.todos.filter(whatever=>whatever._id!==id);
            this.setState({todos:todos})
        })
    }
    toggleTodo(todo){
        const updateURL=APIURL+todo._id;
        fetch(updateURL,{
            method:'put',
            headers: new Headers({
                'Content-Type':'application/json',}),
            body: JSON.stringify(completed)

        })
            .then((resp)=>{
                if(!resp.ok) {
                    if (resp.status >= 400 && resp.status < 500) {
                        return resp.json().then(data => {
                            let err = {whatever: data.message};
                            throw err;
                        })
                    } else {
                        let err = {whatever: 'Please try again later, server is not responding'}
                        throw err;
                    }
                }
                return resp.json();
            }).then(()=>{
            //concatanation array
            const todos=this.state.todos.filter(whatever=>whatever._id!==id);
            this.setState({todos:todos})
        })
    }

    render(){
        //there are for objects here id.. name.. completed..
        const todos =this.state.todos.map((t)=>(<Todoitem key={t._id} {...t} onDelete={this.deleteTodo.bind(this,t._id)}
        onToggle={this.toggleTodo.bind(this,t)}/>));
        return (

            <div>
            <h1>Seattle Love story</h1>
                {/**pass in todo function as props and call it from the todo form*/}
               <TodoForm addTodo={this.addTodo}/>
                <ul>
                    {todos}
                </ul>
            </div>
        )
    }

}
//ES6 features (default is only for one class)
export default TodoList;
